function clear_tasks_content()
{
    $$('#tasks.li').each(function(task) {
        task.remove()
    })
}

//
// Function: load()
// Called by HTML body element's onload event when the widget is ready to start
//
function load()
{
    setupParts()
	tiktrac = null
	
    login_credentials_valid(loadTiktrac, showBack)
    
    Feedback.clear()
}

//
// Function: remove()
// Called when the widget has been removed from the Dashboard
//
function remove()
{
	if (tiktrac && tiktrac.tasks.timer.running)
	{
		tiktrac.tasks.stopTimer()
	}

	widget.setPreferenceForKey(null, "userAccountName")
	widget.setPreferenceForKey(null, "userEmail");
	widget.setPreferenceForKey(null, "userPass");
    widget.setPreferenceForKey(null, "userSheet");
}

